import { useState, useEffect} from 'react';
import './App.css';


// This an arrow function component
const App = () => {

  // This is  Hook
  const [ counter, setCounter ] = useState(0);

  useEffect(() => {
    setCounter(100);
  }, []);

  return (
    <div className="App">
      <button onClick={() => setCounter((prevCount) => prevCount-1)}>-</button>
      <h1>{counter}</h1>
      <button onClick={() => setCounter((prevCount) => prevCount+1)}>+</button>
    </div>
  );
}

export default App;
